def convert(rows):
    res = []
    for row in rows:
        new_row = ""
        for i in range(len(row)):
            ch = row[i]
            if ch == "0":
                new_row += "."
            elif ch == "1":
                new_row += "#"
            else:
                raise ValueError("invalid input")
        res.append(new_row)
    return res

def draw_letter(char):
    font = Libertinus_Serif_18
    ch = char
    rows = font["chars"][ch]
    rows = convert(rows)
    repr = "\n\t".join(rows)
    return rows





